# Split the string into rows (giant list seperated by commas for each row)
import numpy as np

# Split the string into rows
rows = puzzle_input.split('\n')

# Make each row a list
char_lists = [list(row) for row in rows]

# Convert to a 2D NumPy array
array = np.array(char_lists)




def is_it_diagonal_TLBR(array, row, col):
    if row + 3 > array.shape[0] or col + 3 > array.shape[1]:
        return False, None
    
    diagonal_elements = ''.join([array[row+i, col+i] for i in range(3)])

    if diagonal_elements == 'MAS' or diagonal_elements == 'SAM':
        return True, (row+1, col+1)
        
    return False, None
    
def is_it_diagonal_TRBL(array, row, col):
    if row + 3 > array.shape[0] or col - 2 < 0:
        return False, None

    diagonal_elements = ''.join([array[row+i, col-i] for i in range(3)])
    
    if diagonal_elements == 'SAM' or diagonal_elements == 'MAS':
        return True, (row+1, col-1) 
    
    return False, None  
        

locations = []

for row in range(array.shape[0]):
    for col in range(array.shape[1]):
        # Check both diagonal directions
        match1, loc1 = is_it_diagonal_TLBR(array, row, col)
        match2, loc2 = is_it_diagonal_TRBL(array, row, col)
        
        if match1:
            locations.append(loc1)
        
        if match2:
                locations.append(loc2)
          
from collections import Counter

location_counts = Counter(locations)


total_counter=0
for count in location_counts.values():
    if count > 1:
        total_counter+=1
        
print(total_counter)

